contracts/core/ChainlinkPriceProvider.so.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "../interfaces/core/IChainlinkPriceProvider.sol";
import "./PriceProvider.sol";
import "../access/Governable.sol";

/**
 * @title ChainLink's price provider
 * @dev This contract wraps chainlink aggregators
 */
contract ChainlinkPriceProvider is IChainlinkPriceProvider, PriceProvider, Governable {
    using SafeCast for int256;

    uint256 public constant CHAINLINK_DECIMALS = 8;
    uint256 public constant TO_SCALE = 10**(USD_DECIMALS - CHAINLINK_DECIMALS);

    /**
     * @notice Aggregators map (token => aggregator)
     */
    mapping(address => AggregatorV3Interface) public aggregators;

    /// Emitted when an aggregator is updated
    event AggregatorUpdated(address token, AggregatorV3Interface oldAggregator, AggregatorV3Interface newAggregator);

    /// @inheritdoc IPriceProvider
    function getPriceInUsd(address token_)
        public
        view
        virtual
        override(IPriceProvider, PriceProvider)
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        AggregatorV3Interface _aggregator = aggregators[token_];
        require(address(_aggregator) != address(0), "token-without-aggregator");
        int256 _price;
        (, _price, , _lastUpdatedAt, ) = _aggregator.latestRoundData();
        return (_price.toUint256() * TO_SCALE, _lastUpdatedAt);
    }

    /// @inheritdoc IChainlinkPriceProvider
    function updateAggregator(address token_, AggregatorV3Interface aggregator_) external override onlyGovernor {
        require(token_ != address(0), "token-is-null");
        AggregatorV3Interface _current = aggregators[token_];
        require(aggregator_ != _current, "same-as-current");
        _setAggregator(token_, aggregator_);
        emit AggregatorUpdated(token_, _current, aggregator_);
    }

    function _setAggregator(address token_, AggregatorV3Interface aggregator_) internal {
        require(address(aggregator_) == address(0) || aggregator_.decimals() == CHAINLINK_DECIMALS, "invalid-decimals");
        aggregators[token_] = aggregator_;
    }
}

contracts/core/PriceProvider.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "../interfaces/core/IPriceProvider.sol";

/**
 * @title Price providers' super class that implements common functions
 */
abstract contract PriceProvider is IPriceProvider {
    uint256 public constant USD_DECIMALS = 18;

    /// @inheritdoc IPriceProvider
    function getPriceInUsd(address token_) public view virtual returns (uint256 _priceInUsd, uint256 _lastUpdatedAt);

    /// @inheritdoc IPriceProvider
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    )
        external
        view
        virtual
        override
        returns (uint256 _amountOut, uint256 _tokenInLastUpdatedAt, uint256 _tokenOutLastUpdatedAt)
    {
        uint256 _amountInUsd;
        (_amountInUsd, _tokenInLastUpdatedAt) = quoteTokenToUsd(tokenIn_, amountIn_);
        (_amountOut, _tokenOutLastUpdatedAt) = quoteUsdToToken(tokenOut_, _amountInUsd);
    }

    /// @inheritdoc IPriceProvider
    function quoteTokenToUsd(
        address token_,
        uint256 amountIn_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        uint256 _price;
        (_price, _lastUpdatedAt) = getPriceInUsd(token_);
        _amountOut = (amountIn_ * _price) / 10 ** IERC20Metadata(token_).decimals();
    }

    /// @inheritdoc IPriceProvider
    function quoteUsdToToken(
        address token_,
        uint256 amountIn_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        uint256 _price;
        (_price, _lastUpdatedAt) = getPriceInUsd(token_);
        if (_price == 0) {
            return (0, 0);
        }
        _amountOut = (amountIn_ * 10 ** IERC20Metadata(token_).decimals()) / _price;
    }
}

contracts/core/PriceProvidersAggregator..content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/math/Math.sol";
import "../interfaces/core/IPriceProvidersAggregator.sol";
import "../access/Governable.sol";

/**
 * @title Price Providers Aggregator
 */
contract PriceProvidersAggregator is IPriceProvidersAggregator, Governable {
    /**
     * The native token (usually the most liquid asset in the chain)
     * @dev Is used when getting quote from two price providers
     */
    address public immutable nativeToken;

    /**
     * @notice Price providers map
     */
    mapping(DataTypes.Provider => IPriceProvider) public override priceProviders;

    /// Emitted when an price provider is updated
    event PriceProviderUpdated(
        DataTypes.Provider provider,
        IPriceProvider oldPriceProvider,
        IPriceProvider newPriceProvider
    );

    constructor(address nativeToken_) {
        require(nativeToken_ != address(0), "native-token-is-null");
        nativeToken = nativeToken_;
    }

    /// @inheritdoc IPriceProvidersAggregator
    function getPriceInUsd(DataTypes.Provider provider_, address token_)
        external
        view
        override
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        IPriceProvider _provider = priceProviders[provider_];
        require(address(_provider) != address(0), "provider-not-set");
        return _provider.getPriceInUsd(token_);
    }

    /// @inheritdoc IPriceProvidersAggregator
    function quote(
        DataTypes.Provider provider_,
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    )
        external
        view
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        IPriceProvider _provider = priceProviders[provider_];
        require(address(_provider) != address(0), "provider-not-set");
        return _provider.quote(tokenIn_, tokenOut_, amountIn_);
    }

    /// @inheritdoc IPriceProvidersAggregator
    function quote(
        DataTypes.Provider providerIn_,
        address tokenIn_,
        DataTypes.Provider providerOut_,
        address tokenOut_,
        uint256 amountIn_
    )
        public
        view
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _nativeTokenLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        IPriceProvider _providerIn = priceProviders[providerIn_];
        require(address(_providerIn) != address(0), "provider-in-not-set");

        if (providerIn_ == providerOut_) {
            (_amountOut, _tokenInLastUpdatedAt, _tokenOutLastUpdatedAt) = _providerIn.quote(
                tokenIn_,
                tokenOut_,
                amountIn_
            );
            _nativeTokenLastUpdatedAt = block.timestamp;
            return (_amountOut, _tokenInLastUpdatedAt, _nativeTokenLastUpdatedAt, _tokenOutLastUpdatedAt);
        }

        IPriceProvider _providerOut = priceProviders[providerOut_];
        require(address(_providerOut) != address(0), "provider-out-not-set");

        uint256 _nativeTokenLastUpdatedAt0;
        uint256 _nativeTokenLastUpdatedAt1;
        (_amountOut, _tokenInLastUpdatedAt, _nativeTokenLastUpdatedAt0) = _providerIn.quote(
            tokenIn_,
            nativeToken,
            amountIn_
        );
        (_amountOut, _nativeTokenLastUpdatedAt1, _tokenOutLastUpdatedAt) = _providerOut.quote(
            nativeToken,
            tokenOut_,
            _amountOut
        );
        _nativeTokenLastUpdatedAt = Math.min(_nativeTokenLastUpdatedAt0, _nativeTokenLastUpdatedAt1);
    }

    /// @inheritdoc IPriceProvidersAggregator
    function quoteTokenToUsd(
        DataTypes.Provider provider_,
        address token_,
        uint256 amountIn_
    ) external view override returns (uint256 amountOut_, uint256 _lastUpdatedAt) {
        IPriceProvider _provider = priceProviders[provider_];
        require(address(_provider) != address(0), "provider-not-set");
        return _provider.quoteTokenToUsd(token_, amountIn_);
    }

    /// @inheritdoc IPriceProvidersAggregator
    function quoteUsdToToken(
        DataTypes.Provider provider_,
        address token_,
        uint256 amountIn_
    ) external view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        IPriceProvider _provider = priceProviders[provider_];
        require(address(_provider) != address(0), "provider-not-set");
        return _provider.quoteUsdToToken(token_, amountIn_);
    }

    /// @inheritdoc IPriceProvidersAggregator
    function setPriceProvider(DataTypes.Provider provider_, IPriceProvider priceProvider_)
        external
        override
        onlyGovernor
    {
        require(provider_ != DataTypes.Provider.NONE, "invalid-provider");
        IPriceProvider _current = priceProviders[provider_];
        require(priceProvider_ != _current, "same-as-current");

        emit PriceProviderUpdated(provider_, _current, priceProvider_);

        priceProviders[provider_] = priceProvider_;
    }
}

