contracts/core/RedstonePriceProvider.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import {PrimaryProdDataServiceConsumerBase} from "@redstone-finance/evm-connector/contracts/data-services/PrimaryProdDataServiceConsumerBase.sol";
import {IRedstonePriceProvider} from "../interfaces/core/IRedstonePriceProvider.sol";
import {IPriceProvider, PriceProvider} from "./PriceProvider.sol";
import {Governable} from "../access/Governable.sol";

/**
 * @title Redstone's price provider
 */
contract RedstonePriceProvider is
    IRedstonePriceProvider,
    PrimaryProdDataServiceConsumerBase,
    PriceProvider,
    Governable
{
    uint256 public constant REDSTONE_DECIMALS = 8;
    uint256 public constant TO_SCALE = 10 ** (USD_DECIMALS - REDSTONE_DECIMALS);
    uint256 internal constant MAX_TIME_TOLERANCE = 1 minutes;

    struct Cache {
        uint256 price;
        uint256 priceTimestamp;
    }

    /// @notice Feed ids (feedId => token)
    mapping(bytes32 => address[]) internal feeds;

    /// @notice Price cache
    mapping(address => Cache) internal cache;

    /// @notice Emitted when an feed id is updated
    event FeedIdUpdated(bytes32 feedId, address[] tokens);

    /// @notice The cache timestamp was updated.
    /// @param price The Redstone price.
    /// @param priceTimestamp The timestamp contained within the price data packages.
    event CacheUpdated(uint256 price, uint256 priceTimestamp);

    /// @notice Get tokens of a feed
    function tokensOf(bytes32 feedId_) external view returns (address[] memory tokens_) {
        return feeds[feedId_];
    }

    /// @notice Update price for the tokens related to the `dataFeedIds_`
    function updatePrice(bytes32[] memory dataFeedIds_) external {
        (uint256[] memory _values, uint256 _timestamp) = _securelyExtractOracleValuesAndTimestampFromTxMsg(
            dataFeedIds_
        );

        uint256 _valuesLength = _values.length;
        require(_valuesLength == dataFeedIds_.length, "invalid-data");

        _timestamp /= 1000; // Redstone uses milliseconds

        for (uint256 i; i < _valuesLength; ++i) {
            address[] memory _tokens = feeds[dataFeedIds_[i]];
            uint256 _tokensLength = _tokens.length;
            for (uint j; j < _tokensLength; ++j) {
                address _token = _tokens[j];
                require(_token != address(0), "feed-unknown");
                if (_timestamp != cache[_token].priceTimestamp) {
                    cache[_token] = Cache({price: _values[i] * TO_SCALE, priceTimestamp: _timestamp});
                }
            }
        }
    }

    /// @inheritdoc IPriceProvider
    function getPriceInUsd(
        address token_
    )
        public
        view
        virtual
        override(IPriceProvider, PriceProvider)
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        Cache memory _cache = cache[token_];
        _lastUpdatedAt = _cache.priceTimestamp;
        _priceInUsd = _cache.price;

        if (_lastUpdatedAt == 0) {
            return (0, 0);
        }

        if (_lastUpdatedAt < block.timestamp && block.timestamp - _lastUpdatedAt > MAX_TIME_TOLERANCE) {
            return (0, 0);
        }

        if (_lastUpdatedAt > block.timestamp && _lastUpdatedAt - block.timestamp > MAX_TIME_TOLERANCE) {
            return (0, 0);
        }
    }

    /// @inheritdoc IRedstonePriceProvider
    /// @dev The feed should be denominated in USD
    function updateFeed(bytes32 feedId_, address[] memory tokens_) external override onlyGovernor {
        require(feedId_ != bytes32(0), "id-is-null");

        feeds[feedId_] = tokens_;

        emit FeedIdUpdated(feedId_, tokens_);
    }
}

contracts/core/StableCoinProvider.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "../libraries/OracleHelpers.sol";
import "../interfaces/core/IStableCoinProvider.sol";
import "../features/UsingStalePeriod.sol";
import "../features/UsingMaxDeviation.sol";

/**
 * @title Provide pegged stable coin, useful for getting USD prices reference from DEXes
 * @dev This contract mitigates a de-peg scenario by checking price against two stable coins that should be around 1
 */
contract StableCoinProvider is IStableCoinProvider, UsingStalePeriod, UsingMaxDeviation {
    using OracleHelpers for uint256;

    uint256 public constant USD_DECIMALS = 18;
    uint256 public constant ONE_USD = 10**USD_DECIMALS;

    /**
     * @notice A stable coin to use as USD price reference
     * @dev Should not be called directly from other contracts, must use `getStableCoinIfPegged`
     */
    address public primaryStableCoin;
    uint8 private __primaryStableCoinDecimals;

    /**
     * @notice A secondary stable coin used to check USD-peg against primary
     * @dev Should not be called directly from other contracts, must use `getStableCoinIfPegged`
     */
    address public secondaryStableCoin;
    uint8 private __secondaryStableCoinDecimals;

    /// @notice Emitted when stable coin is updated
    event StableCoinsUpdated(
        address oldPrimaryStableCoin,
        address oldSecondaryStableCoin,
        address newPrimaryStableCoin,
        address newSecondaryStableCoin
    );

    constructor(
        address primaryStableCoin_,
        address secondaryStableCoin_,
        uint256 stalePeriod_,
        uint256 maxDeviation_
    ) UsingStalePeriod(stalePeriod_) UsingMaxDeviation(maxDeviation_) {
        _updateStableCoins(primaryStableCoin_, secondaryStableCoin_);
    }

    /// @inheritdoc IStableCoinProvider
    function getStableCoinIfPegged() external view returns (address _stableCoin) {
        // Note: Chainlink supports DAI/USDC/USDT on all chains that we're using
        IPriceProvider _chainlink = addressProvider.providersAggregator().priceProviders(DataTypes.Provider.CHAINLINK);

        (uint256 _priceInUsd, uint256 _lastUpdatedAt) = _chainlink.getPriceInUsd(primaryStableCoin);

        if (!_priceIsStale(primaryStableCoin, _lastUpdatedAt) && _isDeviationOK(_priceInUsd, ONE_USD)) {
            return primaryStableCoin;
        }

        (_priceInUsd, _lastUpdatedAt) = _chainlink.getPriceInUsd(secondaryStableCoin);

        require(
            !_priceIsStale(secondaryStableCoin, _lastUpdatedAt) && _isDeviationOK(_priceInUsd, ONE_USD),
            "stable-prices-invalid"
        );

        return secondaryStableCoin;
    }

    /// @inheritdoc IStableCoinProvider
    function toUsdRepresentation(uint256 stableCoinAmount_) external view returns (uint256 _usdAmount) {
        uint256 _stableCoinDecimals = __primaryStableCoinDecimals;
        if (_stableCoinDecimals == USD_DECIMALS) {
            return stableCoinAmount_;
        }
        _usdAmount = stableCoinAmount_.scaleDecimal(_stableCoinDecimals, USD_DECIMALS);
    }

    /**
     * @notice Update the stable coin keeping correct decimals value
     * @dev Must have both as set or null
     */
    function _updateStableCoins(address primaryStableCoin_, address secondaryStableCoin_) private {
        require(primaryStableCoin_ != address(0) && secondaryStableCoin_ != address(0), "stable-coins-are-null");
        require(primaryStableCoin_ != secondaryStableCoin_, "stable-coins-are-the-same");

        // Update both
        primaryStableCoin = primaryStableCoin_;
        secondaryStableCoin = secondaryStableCoin_;
        __primaryStableCoinDecimals = IERC20Metadata(primaryStableCoin_).decimals();
        __secondaryStableCoinDecimals = IERC20Metadata(secondaryStableCoin_).decimals();
    }

    /**
     * @notice Update stable coin
     * @dev Used externally by the governor
     */
    function updateStableCoins(address primaryStableCoin_, address secondaryStableCoin_) external onlyGovernor {
        emit StableCoinsUpdated(primaryStableCoin, secondaryStableCoin, primaryStableCoin_, secondaryStableCoin_);
        _updateStableCoins(primaryStableCoin_, secondaryStableCoin_);
    }
}

contracts/core/UmbrellaPriceProvider.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@umb-network/toolbox/dist/contracts/IChain.sol";
import "@umb-network/toolbox/dist/contracts/IRegistry.sol";
import "../access/Governable.sol";
import "../interfaces/core/IUmbrellaPriceProvider.sol";
import "./PriceProvider.sol";

/**
 * @notice Umbrella's price provider
 */
contract UmbrellaPriceProvider is IUmbrellaPriceProvider, PriceProvider, Governable {
    bytes32 private constant CHAIN = bytes32("Chain");

    /**
     * @notice token => Umbrella's key mapping (e.g. WBTC => "BTC-USD")
     */
    mapping(address => bytes32) public keyOfToken;

    /**
     * @notice Umbrella's Registry
     * @dev Stores the other Umbrella's contracts' addresses
     */
    IRegistry public immutable registry;

    event KeyOfTokenUpdated(address indexed token, bytes32 oldKey, bytes32 newKey);

    constructor(IRegistry registry_) {
        require(address(registry_) != address(0), "registry-is-null");
        registry = registry_;
    }

    /// @inheritdoc IPriceProvider
    function getPriceInUsd(address token_)
        public
        view
        virtual
        override(IPriceProvider, PriceProvider)
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        (_priceInUsd, _lastUpdatedAt) = _chain().getCurrentValue(keyOfToken[token_]);
        require(_lastUpdatedAt > 0, "invalid-quote");
    }

    /**
     * @notice Get Umbrella's main contract
     */
    function _chain() internal view returns (IChain umbChain) {
        umbChain = IChain(registry.getAddress(CHAIN));
    }

    /**
     * @notice Build key from quote/base string in bytes format
     * @dev The standard parser `bytes32(bytes)` will right pad with zeros
     * but Umbrella expects left padded bytes as key
     * @dev See if there is a simpler way to do the same as this function
     */
    function _toKey(bytes memory quotePairAsBytes_) private pure returns (bytes32) {
        bytes memory _aux = new bytes(32);
        uint256 _len = quotePairAsBytes_.length;
        for (uint256 i; i < _len; ++i) {
            uint256 _idx = 32 - _len + i;
            _aux[_idx] = quotePairAsBytes_[i];
        }
        return bytes32(_aux);
    }

    /**
     * @notice Update Umbrella's key of a token
     * Use `BASE-QUOTE` format (e.g. BTC-USD, ETH-USD, etc)
     */
    function updateKeyOfToken(address token_, string memory quotePair_) external onlyGovernor {
        require(token_ != address(0), "address-is-null");
        bytes32 _currentKey = keyOfToken[token_];
        bytes32 _newKey = _toKey(bytes(quotePair_));
        keyOfToken[token_] = _newKey;
        emit KeyOfTokenUpdated(token_, _currentKey, _newKey);
    }
}

contracts/core/UniswapV2LikePriceProvide.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@uniswap/lib/contracts/libraries/FixedPoint.sol";
import "@uniswap/v2-periphery/contracts/libraries/UniswapV2OracleLibrary.sol";
import "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import "../access/Governable.sol";
import "../interfaces/core/IUniswapV2LikePriceProvider.sol";
import "./PriceProvider.sol";

/**
 * @title UniswapV2 (and forks) TWAP Oracle implementation
 * Based on https://github.com/Uniswap/v2-periphery/blob/master/contracts/examples/ExampleOracleSimple.sol
 */
contract UniswapV2LikePriceProvider is IUniswapV2LikePriceProvider, Governable, PriceProvider {
    using FixedPoint for *;

    /**
     * @notice The UniswapV2-like factory's address
     */
    address public immutable factory;

    /**
     * @notice The native wrapped token (e.g. WETH, WAVAX, WMATIC, etc)
     */
    address public immutable nativeToken;

    /// @inheritdoc IUniswapV2LikePriceProvider
    uint256 public override defaultTwapPeriod;

    struct Oracle {
        address token0;
        address token1;
        uint256 price0CumulativeLast;
        uint256 price1CumulativeLast;
        uint32 blockTimestampLast;
        FixedPoint.uq112x112 price0Average;
        FixedPoint.uq112x112 price1Average;
    }

    /**
     * @notice Oracles'
     * @dev pair => twapPeriod => oracle
     */
    mapping(IUniswapV2Pair => mapping(uint256 => Oracle)) public oracles;

    /// @notice Emitted when default TWAP period is updated
    event DefaultTwapPeriodUpdated(uint256 oldTwapPeriod, uint256 newTwapPeriod);

    constructor(
        address factory_,
        uint256 defaultTwapPeriod_,
        address nativeToken_
    ) {
        require(factory_ != address(0), "factory-is-null");
        defaultTwapPeriod = defaultTwapPeriod_;
        factory = factory_;
        nativeToken = nativeToken_;
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function hasOracle(IUniswapV2Pair pair_) external view override returns (bool) {
        return hasOracle(pair_, defaultTwapPeriod);
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function hasOracle(IUniswapV2Pair pair_, uint256 twapPeriod_) public view override returns (bool) {
        return oracles[pair_][twapPeriod_].blockTimestampLast > 0;
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function pairFor(address token0_, address token1_) public view override returns (IUniswapV2Pair _pair) {
        _pair = IUniswapV2Pair(IUniswapV2Factory(factory).getPair(token0_, token1_));
    }

    /// @inheritdoc IPriceProvider
    function getPriceInUsd(address token_)
        public
        view
        override(IPriceProvider, PriceProvider)
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        return getPriceInUsd(token_, defaultTwapPeriod);
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function getPriceInUsd(address token_, uint256 twapPeriod_)
        public
        view
        override
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        IStableCoinProvider _stableCoinProvider = addressProvider.stableCoinProvider();
        require(address(_stableCoinProvider) != address(0), "stable-coin-not-supported");

        uint256 _stableCoinAmount;
        (_stableCoinAmount, _lastUpdatedAt, ) = quote(
            token_,
            _stableCoinProvider.getStableCoinIfPegged(),
            twapPeriod_,
            10**IERC20Metadata(token_).decimals() // ONE
        );
        _priceInUsd = _stableCoinProvider.toUsdRepresentation(_stableCoinAmount);
    }

    /// @inheritdoc IPriceProvider
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    )
        external
        view
        override(IPriceProvider, PriceProvider)
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        return quote(tokenIn_, tokenOut_, defaultTwapPeriod, amountIn_);
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint256 twapPeriod_,
        uint256 amountIn_
    )
        public
        view
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        if (tokenIn_ == tokenOut_) {
            return (amountIn_, block.timestamp, block.timestamp);
        }

        if (hasOracle(pairFor(tokenIn_, tokenOut_), twapPeriod_)) {
            (_amountOut, _tokenInLastUpdatedAt) = _getAmountOut(tokenIn_, tokenOut_, twapPeriod_, amountIn_);
            _tokenOutLastUpdatedAt = _tokenInLastUpdatedAt;
        } else {
            (_amountOut, _tokenInLastUpdatedAt) = _getAmountOut(tokenIn_, nativeToken, twapPeriod_, amountIn_);
            (_amountOut, _tokenOutLastUpdatedAt) = _getAmountOut(nativeToken, tokenOut_, twapPeriod_, _amountOut);
        }
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function quoteTokenToUsd(
        address token_,
        uint256 amountIn_,
        uint256 twapPeriod_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        uint256 _price;
        (_price, _lastUpdatedAt) = getPriceInUsd(token_, twapPeriod_);
        _amountOut = (amountIn_ * _price) / 10**IERC20Metadata(token_).decimals();
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function quoteUsdToToken(
        address token_,
        uint256 amountIn_,
        uint256 twapPeriod_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        uint256 _price;
        (_price, _lastUpdatedAt) = getPriceInUsd(token_, twapPeriod_);
        _amountOut = (amountIn_ * 10**IERC20Metadata(token_).decimals()) / _price;
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function updateAndQuote(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    )
        external
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        return updateAndQuote(tokenIn_, tokenOut_, defaultTwapPeriod, amountIn_);
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function updateAndQuote(
        address tokenIn_,
        address tokenOut_,
        uint256 twapPeriod_,
        uint256 amountIn_
    )
        public
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        updateOrAdd(tokenIn_, tokenOut_, twapPeriod_);
        return quote(tokenIn_, tokenOut_, twapPeriod_, amountIn_);
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function updateOrAdd(address tokenIn_, address tokenOut_) external override {
        updateOrAdd(tokenIn_, tokenOut_, defaultTwapPeriod);
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function updateOrAdd(
        address tokenIn_,
        address tokenOut_,
        uint256 twapPeriod_
    ) public override {
        IUniswapV2Pair _pair = pairFor(tokenIn_, tokenOut_);
        if (!hasOracle(_pair, twapPeriod_)) {
            _addOracleFor(_pair, twapPeriod_);
        }
        _updateIfNeeded(_pair, twapPeriod_);
    }

    /**
     * @notice Create new oracle
     * @param pair_ The pair to get prices from
     * @param twapPeriod_ The TWAP period
     */
    function _addOracleFor(IUniswapV2Pair pair_, uint256 twapPeriod_) private {
        require(address(pair_) != address(0), "invalid-pair");

        (uint112 _reserve0, uint112 _reserve1, uint32 _blockTimestampLast) = pair_.getReserves();

        require(_reserve0 != 0 && _reserve1 != 0, "no-reserves");

        oracles[pair_][twapPeriod_] = Oracle({
            token0: pair_.token0(),
            token1: pair_.token1(),
            price0CumulativeLast: pair_.price0CumulativeLast(),
            price1CumulativeLast: pair_.price1CumulativeLast(),
            blockTimestampLast: _blockTimestampLast,
            price0Average: uint112(0).encode(),
            price1Average: uint112(0).encode()
        });
    }

    /**
     * @notice Get the output amount for a given oracle
     * @param tokenIn_ The address of assetIn
     * @param tokenOut_ The address of assetOut
     * @param twapPeriod_ The TWAP period
     * @param amountIn_ Amount of input token
     * @return _amountOut Amount out
     * @return _lastUpdatedAt Last updated timestamp
     */
    function _getAmountOut(
        address tokenIn_,
        address tokenOut_,
        uint256 twapPeriod_,
        uint256 amountIn_
    ) private view returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        Oracle memory _oracle = oracles[pairFor(tokenIn_, tokenOut_)][twapPeriod_];
        if (tokenIn_ == _oracle.token0) {
            _amountOut = _oracle.price0Average.mul(amountIn_).decode144();
        } else {
            _amountOut = _oracle.price1Average.mul(amountIn_).decode144();
        }
        _lastUpdatedAt = _oracle.blockTimestampLast;
    }

    /**
     * @notice Update an oracle
     * @param pair_ The pair to update
     * @param twapPeriod_ The TWAP period
     * @return True if updated was performed
     */
    function _updateIfNeeded(IUniswapV2Pair pair_, uint256 twapPeriod_) private returns (bool) {
        Oracle storage _oracle = oracles[pair_][twapPeriod_];

        (uint256 price0Cumulative, uint256 price1Cumulative, uint32 blockTimestamp) = UniswapV2OracleLibrary
            .currentCumulativePrices(address(pair_));
        uint32 timeElapsed;
        unchecked {
            timeElapsed = blockTimestamp - _oracle.blockTimestampLast; // overflow is desired
        }
        // ensure that at least one full period has passed since the last update
        if (timeElapsed < twapPeriod_) return false;

        uint256 price0new;
        uint256 price1new;

        unchecked {
            price0new = price0Cumulative - _oracle.price0CumulativeLast;
            price1new = price1Cumulative - _oracle.price1CumulativeLast;
        }

        // overflow is desired, casting never truncates
        // cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
        _oracle.price0Average = FixedPoint.uq112x112(uint224(price0new / timeElapsed));
        _oracle.price1Average = FixedPoint.uq112x112(uint224(price1new / timeElapsed));
        _oracle.price0CumulativeLast = price0Cumulative;
        _oracle.price1CumulativeLast = price1Cumulative;
        _oracle.blockTimestampLast = blockTimestamp;
        return true;
    }

    /// @inheritdoc IUniswapV2LikePriceProvider
    function updateDefaultTwapPeriod(uint256 newDefaultTwapPeriod_) external override onlyGovernor {
        emit DefaultTwapPeriodUpdated(defaultTwapPeriod, newDefaultTwapPeriod_);
        defaultTwapPeriod = newDefaultTwapPeriod_;
    }
}

contracts/core/UniswapV3PriceProvider.so.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "@openzeppelin/contracts/utils/math/SafeCast.sol";
import "../access/Governable.sol";
import "../interfaces/utils/IUniswapV3CrossPoolOracle.sol";
import "../interfaces/core/IUniswapV3PriceProvider.sol";
import "./PriceProvider.sol";

contract UniswapV3PriceProvider is IUniswapV3PriceProvider, Governable, PriceProvider {
    /**
     * @notice The UniswapV3CrossPoolOracle contract
     * @dev This contract encapsulates UniswapV3 oracle logic
     */
    IUniswapV3CrossPoolOracle public immutable crossPoolOracle;

    /**
     * @notice The time-weighted average price (TWAP) period
     * @dev See more: https://docs.uniswap.org/protocol/concepts/V3-overview/oracle
     */
    uint32 public override defaultTwapPeriod;

    /**
     * @notice The default pool fee to use
     * @dev Use 1e6 for 100% (e.g 3000 is 0.3%)
     */
    uint24 public defaultPoolFee;

    /// @notice Emitted when the default TWAP period is updated
    event DefaultTwapPeriodUpdated(uint32 oldDefaultTwapPeriod, uint32 newDefaultTwapPeriod);

    /// @notice Emitted when the default pool fee updated
    event DefaultPoolFeeUpdated(uint24 oldDefaultPoolFee, uint24 newDefaultPoolFee);

    constructor(
        IUniswapV3CrossPoolOracle crossPoolOracle_,
        uint32 defaultTwapPeriod_,
        uint24 defaultFee_
    ) {
        require(address(crossPoolOracle_) != address(0), "cross-pool-is-null");
        crossPoolOracle = crossPoolOracle_;
        defaultTwapPeriod = defaultTwapPeriod_;
        defaultPoolFee = defaultFee_;
    }

    /// @inheritdoc IPriceProvider
    function getPriceInUsd(address token_)
        public
        view
        override(IPriceProvider, PriceProvider)
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        return getPriceInUsd(token_, defaultPoolFee, defaultTwapPeriod);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function getPriceInUsd(address token_, uint32 twapPeriod_)
        public
        view
        override
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        return getPriceInUsd(token_, defaultPoolFee, twapPeriod_);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function getPriceInUsd(address token_, uint24 poolFee_)
        public
        view
        override
        returns (uint256 _priceInUsd, uint256 _lastUpdatedAt)
    {
        return getPriceInUsd(token_, poolFee_, defaultTwapPeriod);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function getPriceInUsd(
        address token_,
        uint24 poolFee_,
        uint32 twapPeriod_
    ) public view override returns (uint256 _priceInUsd, uint256 _lastUpdatedAt) {
        IStableCoinProvider _stableCoinProvider = addressProvider.stableCoinProvider();
        require(address(_stableCoinProvider) != address(0), "stable-coin-not-supported");

        uint256 _stableCoinAmount;
        (_stableCoinAmount, _lastUpdatedAt, ) = quote(
            token_,
            _stableCoinProvider.getStableCoinIfPegged(),
            poolFee_,
            twapPeriod_,
            10**IERC20Metadata(token_).decimals() // ONE
        );

        _priceInUsd = _stableCoinProvider.toUsdRepresentation(_stableCoinAmount);
    }

    /// @inheritdoc IPriceProvider
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    )
        external
        view
        override(IPriceProvider, PriceProvider)
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        return quote(tokenIn_, tokenOut_, defaultPoolFee, defaultTwapPeriod, amountIn_);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint32 twapPeriod_,
        uint256 amountIn_
    )
        external
        view
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        return quote(tokenIn_, tokenOut_, defaultPoolFee, twapPeriod_, amountIn_);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint24 poolFee_,
        uint256 amountIn_
    )
        external
        view
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        return quote(tokenIn_, tokenOut_, poolFee_, defaultTwapPeriod, amountIn_);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint24 poolFee_,
        uint32 twapPeriod_,
        uint256 amountIn_
    )
        public
        view
        override
        returns (
            uint256 _amountOut,
            uint256 _tokenInLastUpdatedAt,
            uint256 _tokenOutLastUpdatedAt
        )
    {
        if (tokenIn_ == tokenOut_) {
            return (amountIn_, block.timestamp, block.timestamp);
        }

        if (tokenIn_ == crossPoolOracle.nativeToken()) {
            _amountOut = crossPoolOracle.ethToAsset(amountIn_, tokenOut_, poolFee_, twapPeriod_);
        } else if (tokenOut_ == crossPoolOracle.nativeToken()) {
            _amountOut = crossPoolOracle.assetToEth(tokenIn_, amountIn_, poolFee_, twapPeriod_);
        } else {
            _amountOut = crossPoolOracle.assetToAsset(tokenIn_, amountIn_, tokenOut_, poolFee_, twapPeriod_);
        }
        _tokenInLastUpdatedAt = block.timestamp;
        _tokenOutLastUpdatedAt = block.timestamp;
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quoteTokenToUsd(
        address token_,
        uint256 amountIn_,
        uint24 poolFee_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        return quoteTokenToUsd(token_, amountIn_, poolFee_, defaultTwapPeriod);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quoteTokenToUsd(
        address token_,
        uint256 amountIn_,
        uint32 twapPeriod_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        return quoteTokenToUsd(token_, amountIn_, defaultPoolFee, twapPeriod_);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quoteTokenToUsd(
        address token_,
        uint256 amountIn_,
        uint24 poolFee_,
        uint32 twapPeriod_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        uint256 _price;
        (_price, _lastUpdatedAt) = getPriceInUsd(token_, poolFee_, twapPeriod_);
        _amountOut = (amountIn_ * _price) / 10**IERC20Metadata(token_).decimals();
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quoteUsdToToken(
        address token_,
        uint256 amountIn_,
        uint24 poolFee_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        return quoteUsdToToken(token_, amountIn_, poolFee_, defaultTwapPeriod);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quoteUsdToToken(
        address token_,
        uint256 amountIn_,
        uint32 twapPeriod_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        return quoteUsdToToken(token_, amountIn_, defaultPoolFee, twapPeriod_);
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function quoteUsdToToken(
        address token_,
        uint256 amountIn_,
        uint24 poolFee_,
        uint32 twapPeriod_
    ) public view override returns (uint256 _amountOut, uint256 _lastUpdatedAt) {
        uint256 _price;
        (_price, _lastUpdatedAt) = getPriceInUsd(token_, poolFee_, twapPeriod_);
        _amountOut = (amountIn_ * 10**IERC20Metadata(token_).decimals()) / _price;
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function updateDefaultTwapPeriod(uint32 newDefaultTwapPeriod_) external onlyGovernor {
        emit DefaultTwapPeriodUpdated(defaultTwapPeriod, newDefaultTwapPeriod_);
        defaultTwapPeriod = newDefaultTwapPeriod_;
    }

    /// @inheritdoc IUniswapV3PriceProvider
    function updateDefaultPoolFee(uint24 newDefaultPoolFee_) external onlyGovernor {
        emit DefaultPoolFeeUpdated(defaultPoolFee, newDefaultPoolFee_);
        defaultPoolFee = newDefaultPoolFee_;
    }
}

