// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "../periphery/IOracle.sol";
import "./IExchange.sol";

/**
 * @notice Swapper interface
 * @dev This contract doesn't support native coins (e.g. ETH, AVAX, MATIC, etc) use wrapper tokens instead
 */
interface ISwapper {
    /**
     * @notice The list of supported DEXes
     * @dev This function is gas intensive
     */
    function getAllExchanges() external view returns (address[] memory);

    /**
     * @notice The list of main DEXes
     * @dev This function is gas intensive
     */
    function getMainExchanges() external view returns (address[] memory);

    /**
     * @notice Oracle to get prices from
     * @dev Is used combined with `slippage` in order to check swaps outcomes and reject if aren't acceptable
     */
    function oracle() external view returns (IOracle);

    /**
     * @notice Get max acceptable slippage
     * @dev Swaps will revert if actual output from swap is too far from oracle price
     */
    function maxSlippage() external view returns (uint256);

    /**
     * @notice Get *spot* quote
     * It will return the swap amount based on the current reserves of the best pair/path found (i.e. spot price).
     * @dev It shouldn't be used as oracle!!!
     */
    function getBestAmountIn(
        address tokenIn_,
        address tokenOut_,
        uint256 amountOut_
    )
        external
        returns (
            uint256 _amountInMax,
            IExchange _exchange,
            bytes memory _path
        );

    /**
     * @notice Get *spot* quote
     * It will return the swap amount based on the current reserves of the best pair/path found (i.e. spot price).
     * @dev It shouldn't be used as oracle!!!
     */
    function getBestAmountOut(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    )
        external
        returns (
            uint256 _amountOutMin,
            IExchange _exchange,
            bytes memory _path
        );

    /**
     * @notice Perform an exact input swap
     */
    function swapExactInput(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_,
        address _receiver
    ) external returns (uint256 _amountOut);

    /**
     * @notice Perform an exact output swap
     */
    function swapExactOutput(
        address tokenIn_,
        address tokenOut_,
        uint256 amountOut_,
        address _receiver
    ) external returns (uint256 _amountIn);
}

contracts/interfaces/periphery/IOracle.s.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

interface IOracle {
    /**
     * @notice Get USD (or equivalent) price of an asset
     * @param token_ The address of asset
     * @return _priceInUsd The USD price
     */
    function getPriceInUsd(address token_) external view returns (uint256 _priceInUsd);

    /**
     * @notice Get quote
     * @param tokenIn_ The address of assetIn
     * @param tokenOut_ The address of assetOut
     * @param amountIn_ Amount of input token
     * @return _amountOut Amount out
     */
    function quote(
        address tokenIn_,
        address tokenOut_,
        uint256 amountIn_
    ) external view returns (uint256 _amountOut);

    /**
     * @notice Get quote in USD (or equivalent) amount
     * @param token_ The address of assetIn
     * @param amountIn_ Amount of input token.
     * @return amountOut_ Amount in USD
     */
    function quoteTokenToUsd(address token_, uint256 amountIn_) external view returns (uint256 amountOut_);

    /**
     * @notice Get quote from USD (or equivalent) amount to amount of token
     * @param token_ The address of assetOut
     * @param amountIn_ Input amount in USD
     * @return _amountOut Output amount of token
     */
    function quoteUsdToToken(address token_, uint256 amountIn_) external view returns (uint256 _amountOut);
}

contracts/periphery/tokens/YEarnTokenOra.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.9;

import "../../interfaces/periphery/ITokenOracle.sol";
import "../../interfaces/periphery/IOracle.sol";
import "../../interfaces/external/yearn/IYearn.sol";

/**
 * @title Oracle for Yearn tokens
 */
contract YEarnTokenOracle is ITokenOracle {
    /// @inheritdoc ITokenOracle
    function getPriceInUsd(address token_) external view returns (uint256 _priceInUsd) {
        IYearn _yToken = IYearn(token_);
        uint256 _underlyingPrice = IOracle(msg.sender).getPriceInUsd(_yToken.token());
        return (_yToken.getPricePerFullShare() * _underlyingPrice) / 1e18; // getPricePerFullShare is scaled by 1e18
    }
}

